import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { AngularFireAuth } from '@angular/fire/compat/auth';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/Service/auth.service';

@Component({
  selector: 'app-chat',
  templateUrl: './chat.component.html',
  styleUrls: ['./chat.component.css']
})
export class ChatComponent implements OnInit {


  constructor(public auth: AngularFireAuth, private http: HttpClient, private router: Router, public _serviceAuth: AuthService) { }

  ngOnInit(): void {
    if (this._serviceAuth.user == null) {
      this.router.navigate(['/'])
    }
  }

  getEmailInitial(): string {
    const email = this._serviceAuth.user.multiFactor.user.email;
    if (email && email.length > 0) {
      return email.charAt(0).toUpperCase(); // Retourne le premier caractère en majuscule
    } else {
      return ''; // Retourne une chaîne vide si l'e-mail est vide
    }
  }

  apiUrl = 'https://api.openai.com/v1/chat/completions';

  userInput: string = '';
  result: any;

  messages: { role: string; content: string; }[] = [];

  httpHeaders = new HttpHeaders().set("Authorization", "Bearer sk-proj-JjuvIUY42zwBwPRLkHW5T3BlbkFJgeGlx2Vwg3VEMOehREtT");

  sendMessage() {
    // Ajouter le message de l'utilisateur aux messages
    this.messages.push({ role: "user", content: this.userInput });

    // Construire le payload avec les messages actuels
    const payload = {
      model: "gpt-3.5-turbo",
      messages: this.messages
    };

    this.http.post(this.apiUrl, payload, { headers: this.httpHeaders }).subscribe({
      next: (resp) => {
        this.result = resp;
        // Ajouter la réponse de l'API à l'historique du chat
        this.messages.push({ role: "system", content: this.result.choices[0].message.content });
        // Réinitialiser l'entrée utilisateur
        this.userInput = '';
      },
      error: (err) => {
        console.error('Erreur lors de l\'envoi du message :', err);
      }
    });
  }

  onKeyPress(event: KeyboardEvent) {
    if (event.key === "Enter") {
      this.sendMessage();
    }
  }

  signOut() {
    this.auth.signOut()
      .then(() => {
        this._serviceAuth.user = null
        console.log('Déconnexion réussie!');
        this.router.navigate(['/'])

      })
      .catch((error) => {
        console.error('Erreur lors de la déconnexion:', error);
      });
  }
}
<link
  href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.3/dist/css/bootstrap.min.css"
  rel="stylesheet"
  integrity="sha384-QWTKZyjpPEjISv5WaRU9OFeRpok6YctnYmDr5pNlyT2bRjXh0JMhjY6hW+ALEwIH"
  crossorigin="anonymous"
/>

<body>
  <div class="container">
    <div class="row clearfix">
      <div class="col-lg-12">
        <div class="chat">
          <div class="chat-header clearfix">
            <div class="row">
              <div class="col-lg-6" style="display: flex">
                <div class="rounded-box"><span>{{ getEmailInitial() }}</span></div>
                <div>
                  <h5> {{_serviceAuth.user.multiFactor.user.email}} </h5>
                  <p style="margin-top: -6px; font-size: small">
                   {{_serviceAuth.user.multiFactor.user.uid}}
                  </p>
                  <button (click)="signOut()" class="btn btn-warning">Déconnexion</button>

                </div>
              </div>
            </div>
          </div>
          <div class="chat-history" style="height: 480px; overflow-y: auto">
            <ul *ngFor="let message of messages">
              <li class="clearfix">
                <div
                  class="message my-message-chat"
                  *ngIf="message.role === 'user'"
                >
                  {{ message.content }}
                </div>
              </li>
              <li class="clearfix">
                <div
                  class="message my-message-user"
                  *ngIf="message.role === 'system'"
                >
                  <pre class="message-content"> {{ message.content }} </pre>
                </div>
              </li>
            </ul>
          </div>
          <div class="chat-message clearfix">
            <div class="chat-input">
              <input
                [(ngModel)]="userInput"
                type="text "
                class="fill"
                (keypress)="onKeyPress($event)"
                placeholder="Tapez un message..."
              />
              <button
                (click)="sendMessage()"
                style="color: black"
                id="sendButton"
              >
                Envoyer
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</body>
<!-- chat.component.html -->
